import React from "react";
import FormInput from "../form-input/form-input.component";
import CustomButton from "../custom-button/custom-button.component";
import {auth} from '../../firebase/firebase.utils';
import './registraion.styles.scss';
import { async } from "@firebase/util";
class Registraion extends React.Component{
    constructor(){
        super();
        this.state={
            displayName:"",
            email:"",
            password: "",
            confirmPassword: ""
        };

    }
    
    // handleSubmit= async event =>{
    //     event.preventDefault();
    //     const {displayName, email, password, confirmPassword}=this.state;
    //     if(password!=confirmPassword){
    //         alert("Passwords don't matched");
    //         return;
    //     }
    //     try {
    //         const {user}= await auth.createUserWithEmailAndPassword(email,password);

    //         await createUserProfileDocument(user,{displayName});
    //         this.setState({
    //             displayName:"",
    //             email:"",
    //             password: "",
    //             confirmPassword: ""
    //         });
    //     } catch (error) {
    //         console.error(error);
    //     }
    // };

    handleChange=event=>{
        const {name,value}=event.target;
        this.setState({[name]:value});
    };
    render(){
        return(
            <div className="registraion">
               <h2 className="title">I don't have an account</h2>
               <span>Sign up your email password</span>
               <form className="registraion-form" onSubmit={this.handleSubmit}>
                <FormInput
                type='text'
                name='displayName'
                value={this.state.displayName}
                onChange={this.handleChange}
                label='Display Name'
                required
                >
                </FormInput>
                <FormInput
                type='email'
                name='email'
                value={this.state.email}
                onChange={this.handleChange}
                label='Email'
                required
                >
                </FormInput>
                <FormInput
                type='password'
                name='pawwword'
                value={this.state.pawwword}
                onChange={this.handleChange}
                label='Password'
                required
                >
                </FormInput>
                <FormInput
                type='password'
                name='confirmPassword'
                value={this.state.confirmPassword}
                onChange={this.handleChange}
                label='Confirm Password'
                required
                >
                </FormInput>
                <CustomButton type='submit'>Register</CustomButton>

               </form>
            </div>
        );
    }
}

export default Registraion;
